    private void bt_m_in_eActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_m_in_eActionPerformed
        // TODO add your handling code here:
        double startmeter, endmeter, yard, inch, feet;

        String ausgabe = "meter:   inch:     feet:     yard:";
        startmeter = Double.parseDouble(txf_startmeter.getText());
        endmeter = Double.parseDouble(txf_endmeter.getText());

        while (startmeter <= endmeter) {

            inch = startmeter * 100 / 2.54;
            feet = startmeter / 0.3048;
            yard = startmeter / 0.9144;

            ausgabe = ausgabe + "\n" + String.format("%6.2f %8.2f %8.2f %8.2f\n", startmeter, inch, feet, yard);

            startmeter++;

        }
        txa_ausgabe.setText(ausgabe);
    }//GEN-LAST:event_bt_m_in_eActionPerformed

    private void bt_e_in_mActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_e_in_mActionPerformed
        // TODO add your handling code here:
        double startmeter, endmeter, meter, cm, mm;
        String ausgabe = "meter:    M:         CM:    MM:";
        startmeter = Double.parseDouble(txf_startmeter.getText());
        endmeter = Double.parseDouble(txf_endmeter.getText());

        while (startmeter <= endmeter) {

            meter = startmeter / 39.37;
            cm = startmeter *2.54;
            mm = startmeter *25.4;

            ausgabe = ausgabe + "\n" + String.format("%6.2f %8.2f %8.2f %8.2f\n", startmeter, meter, cm, mm);

            startmeter++;

        }
        txa_ausgabe.setText(ausgabe);
    }//GEN-LAST:event_bt_e_in_mActionPerformed

